
public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int x =341;
byte b;
short c;
int y = 65537;

b = (byte) x;
c = (short)y;
System.out.println("value of b in byte is:" + b);
System.out.println("value of c in short is:" + c);		
	}

}

  
